CREATE TABLE country (
    countryNameEn             VARCHAR(255),
    countryNameLocal          TEXT,
    countryCode               CHAR(5),
    currencyCode              CHAR(5),
    currencyNameEn            VARCHAR(255),
    officialLanguageCode      CHAR(10),
    officialLanguageNameEn    VARCHAR(255),
    officialLanguageNameLocal VARCHAR(255),
    countryCallingCode        VARCHAR(10),
    areaCodes                 TEXT,
    region                    VARCHAR(255),
    flag                      VARCHAR(5)
);

